arr1=(`iptables -L PREROUTING -n -t nat --line-number |grep DNAT|grep "dpt:@localPort "|sort -r|awk '{print $1,$3,$9}'|tr " " ":"|tr "\n" " "`);for cell in ${arr1[@]};do arr2=(`echo $cell|tr ":" " "`);index=${arr2[0]};proto=${arr2[1]};targetIP=${arr2[3]};targetPort=${arr2[4]};iptables -t nat  -D PREROUTING $index;toRmIndexs=(`iptables -L POSTROUTING -n -t nat --line-number|grep $targetIP|grep $targetPort|grep $proto|awk  '{print $1}'|sort -r|tr "\n" " "`);for cell1 in ${toRmIndexs[@]};do iptables -t nat  -D POSTROUTING $cell1;done;done
